userDefinedLinkHintCss:
div > .internalVimiumHintMarker.vimiumHintMarker {
  background: white;
  border: 1px solid black;
  box-shadow: none;
  border-radius: 0;
}
div > .internalVimiumHintMarker.vimiumHintMarker span {
  color: black;
  font-weight: bold;
  font-size: 12px;
  text-shadow: none;
}
div > .internalVimiumHintMarker.vimiumHintMarker > .matchingCharacter {
  color: red;
}

div.vimiumHUD {
  box-shadow: none;
  background: white;
  border: 1px solid black;
  border-radius: 0;
  padding: 0;
}
div.vimiumHUD .hud-find {
  border: none;
}
div.vimiumHUD .vimiumHUDSearchArea {
  background: none;
}

#vomnibar {
  background: white;
  border-radius: 0;
  box-shadow: none;
  border: 1px solid black;
}
#vomnibar .vomnibarSearchArea {
  border-radius: 0;
  border-bottom: none;
  background: none;
}
#vomnibar input {
  border-radius: 0;
  border: none;
  box-shadow: none;
  height: auto;
}
#vomnibar ul {
  background: none;
}
#vomnibar .vomnibarMatch {
  color: red !important;
}
